INSERT INTO planta (nome, familia, habitat, uso_comum, ciclo_de_vida, caracteristicas_especiais, imagem, min_umidade, max_umidade, min_temperatura, max_temperatura, min_altura, max_altura, deletado) VALUES
('Abacaxi-roxo', 'Commelinaceae', 'México e América Central', 'Planta ornamental para jardins e forrações. Cobertura de solo em áreas sombreadas. Ideal para bordaduras e maciços.', 'Perene', 'Toxicidade baixa a moderada para pets, podendo causar irritação gastrointestinal. Folhas de cor roxa na parte inferior.', 'https://img.freepik.com/fotos-gratis/tradescantia-spathacea-abacaxi-roxo-planta-de-ornamento_181624-52622.jpg', 60, 90, 18, 30, 20, 40, false),
('Alamanda', 'Apocynaceae', 'Brasil', 'Arbusto ornamental de sol pleno, trepadeira que pode ser usada em cercas e caramanchões.', 'Perene', 'Planta tóxica, com seiva que causa problemas gastrointestinais se ingerida. Floresce intensamente e quase o ano todo. Exige cuidado no manuseio.', 'https://www.shutterstock.com/search/allamanda-cathartica-l', 60, 80, 20, 35, 100, 300, false),
('Alecrim', 'Lamiaceae', 'Mediterrâneo', 'Erva aromática e medicinal, usada na culinária e em paisagismo. Atrai polinizadores.', 'Perene', 'Não tóxico em doses normais, mas pode apresentar toxicidade em altas concentrações. Resistente e de baixa manutenção. Possui propriedades anti-inflamatórias.', 'https://br.freepik.com/fotos/alecrim', 30, 60, 10, 30, 30, 80, false),
('Antúrio', 'Araceae', 'América tropical', 'Planta de interior e flor ornamental, apreciada por suas flores duráveis.', 'Perene', 'Tóxica para humanos e pets, contendo cristais de oxalato de cálcio que causam irritação na pele e mucosas. Floresce o ano todo em condições ideais.', 'https://www.shutterstock.com/search/anturio-flower', 60, 80, 18, 30, 40, 100, false),
('Avenca', 'Pteridaceae', 'Regiões tropicais úmidas', 'Planta de interior popular, ideal para vasos suspensos ou prateleiras.', 'Perene', 'Não tóxica para humanos e animais. É muito sensível a ventos e luz solar direta. Simboliza delicadeza e elegância.', 'https://www.shutterstock.com/search/adiantum-raddianum', 60, 90, 15, 28, 20, 50, false),
('Azaleia', 'Ericaceae', 'Ásia', 'Arbusto florífero para paisagismo, bordaduras e vasos.', 'Perene', 'Extremamente tóxica para cães e gatos, podendo causar vômito, diarreia e alterações neurológicas. Contém graianotoxina, uma substância perigosa.', 'https://www.shutterstock.com/search/rhododendron-simsii', 60, 85, 15, 25, 40, 150, false),
('Bambu da sorte', 'Asparagaceae', 'África Tropical', 'Planta de interior, usada em arranjos e como decoração. Simboliza prosperidade.', 'Perene', 'Tóxica para cães e gatos, pode causar problemas gastrointestinais. Apesar do nome, não é um bambu verdadeiro. É uma planta resistente e de fácil cuidado, podendo ser cultivada em água ou solo.', 'https://www.sitiodamata.com.br/media/catalog/product/cache/1/image/600x600/9df78eab33525d08d6e5fb8d27136e95/b/a/bambu-da-sorte.jpg', 40, 70, 18, 32, 50, 100, false),
('Begônia', 'Begoniaceae', 'América tropical', 'Planta de interior com folhas ornamentais e flores delicadas. Ideal para locais com luz difusa e alta umidade.', 'Perene', 'Toxicidade moderada para pets, principalmente as raízes. Causa vômitos e irritação na boca se ingerida. É uma das 10 maiores famílias de plantas angiospermas.', 'https://www.shutterstock.com/search/begonia-maculata-polka-dot-plant', 50, 85, 15, 28, 20, 60, false),
('Bico de papagaio', 'Euphorbiaceae', 'América Central e México', 'Arbusto ornamental, muito popular em épocas festivas. Usado em jardins e vasos.', 'Perene', 'Látex tóxico que causa irritação na pele. A ingestão pode provocar vômitos e diarreia. Requer cuidado com animais de estimação e crianças.', 'https://www.missouribotanicalgarden.org/PlantFinder/PlantFinderDetails.aspx?taxonid=280089&isprofile=1&basic=poinsettia', 60, 80, 18, 32, 50, 200, false),
('Bromélia', 'Bromeliaceae', 'América tropical', 'Planta ornamental e de interior, versátil para ambientes internos e externos.', 'Perene', 'A maioria das bromélias não é tóxica. Espécies como a *Bromelia antiacantha* podem ter toxicidade. Absorve poluentes do ar e tem baixa manutenção.', 'https://www.shutterstock.com/search/bromelia', 40, 80, 15, 30, 15, 60, false),
('Buganvília', 'Nyctaginaceae', 'América do Sul', 'Trepadeira vigorosa para jardins, cercas vivas e pergolados.', 'Perene', 'Considerada não tóxica, mas seus espinhos podem causar danos físicos. Floresce em cores vibrantes durante grande parte do ano. Atrai polinizadores.', 'https://www.shutterstock.com/search/bougainvillea-glabra-choisy', 50, 80, 18, 35, 200, 600, false),
('Cacto rabo de macaco', 'Cactaceae', 'Bolívia e Argentina', 'Cacto ornamental e pendente, ideal para vasos e cestos suspensos.', 'Perene', 'Não tóxico para humanos e pets, mas seus espinhos macios podem causar irritação na pele. Cacto de crescimento rápido e fácil de propagar que atrai polinizadores.', 'https://www.floramorumby.com.br/wp-content/uploads/2021/04/cacto-rabo-de-macaco.jpg', 10, 30, 18, 35, 50, 150, false),
('Cacto rabos pendentes', 'Cactaceae', 'América do Sul', 'Cactos com caules pendentes, ideais para cestos e vasos suspensos.', 'Perene', 'Geralmente não tóxicos, mas a presença de espinhos pode causar danos físicos. São resistentes e de baixa manutenção, atraindo polinizadores.', 'https://www.shutterstock.com/search/aporocactus', 10, 30, 18, 35, 30, 100, false),
('Calathea', 'Marantaceae', 'América tropical', 'Planta de interior com folhas ornamentais. Ideal para locais com sombra e alta umidade.', 'Perene', 'Não tóxica para animais e humanos, sendo pet friendly. Conhecida como "planta rezadeira" porque suas folhas se fecham à noite. Precisa de alta umidade, luz indireta e solo úmido.', 'https://www.istockphoto.com/br/foto/calathea-warscewiczii-gm875496224-244411770', 60, 80, 18, 28, 30, 70, false),
('Camélia', 'Theaceae', 'Ásia', 'Arbusto ornamental de flores vistosas e elegantes. Usado em jardins e parques.', 'Perene', 'Não tóxica para humanos e animais, tornando-a uma opção segura. Melhora a qualidade do ar em ambientes fechados.', 'https://www.shutterstock.com/search/camellia-japonica', 50, 80, 15, 25, 50, 300, false),
('Cica', 'Cycadaceae', 'Japão e Indonésia', 'Planta ornamental exótica, ideal para jardins, entradas e paisagismo sofisticado.', 'Perene', 'Altamente tóxica para humanos e pets, especialmente as sementes que contêm a toxina cicassina. Causa vômito, diarreia, convulsões e insuficiência hepática. Planta de crescimento lento que tolera geadas.', 'https://www.shutterstock.com/search/cycas-revoluta', 40, 70, 15, 38, 50, 200, false),
('Clorofito', 'Asparagaceae', 'África tropical', 'Planta ornamental de interior, ideal para vasos suspensos ou prateleiras.', 'Perene', 'Não tóxica, mas a ingestão em grandes quantidades pode causar desconforto gastrointestinal. Purifica o ar e é de fácil cuidado.', 'https://www.sitiodamata.com.br/media/catalog/product/cache/1/image/600x600/9df78eab33525d08d6e5fb8d27136e95/c/l/clorofito_1.jpg', 40, 70, 15, 30, 20, 60, false),
('Comigo-Ninguém-Pode', 'Araceae', 'América tropical, Brasil', 'Planta de interior com folhas grandes e marcantes, ideal para decoração.', 'Perene', 'Extremamente tóxica para humanos e pets. Contém cristais de oxalato de cálcio que causam irritação severa, edema e asfixia se ingeridos. Resiste a ambientes com pouca luz e umidade.', 'https://www.shutterstock.com/search/dieffenbachia', 40, 70, 15, 25, 50, 200, false),
('Copo-de-leite', 'Araceae', 'América do Sul', 'Flor ornamental, popular em arranjos e jardins úmidos.', 'Perene', 'Tóxica para humanos e animais, contém cristais de oxalato de cálcio. Usada em paisagismo em áreas com alta umidade.', 'https://www.shutterstock.com/image-photo/zantedeschia-aethiopica-brazilian-copo-de-leite-1686860368', 50, 80, 15, 28, 40, 100, false),
('Costela-de-Adão', 'Araceae', 'América Central', 'Planta de interior com folhas grandes e recortadas. Purificadora de ar.', 'Perene', 'Tóxica para humanos e pets, possui cristais de oxalato de cálcio que causam irritação na pele, vômitos e diarreia. Floresce o ano todo em condições ideais.', 'https://www.shutterstock.com/search/monstera-deliciosa', 60, 85, 18, 32, 50, 300, false),
('Crotón', 'Euphorbiaceae', 'Sul da Ásia, Ilhas do Pacífico', 'Arbusto ornamental com folhas coloridas e vibrantes. Usado em paisagismo e decoração.', 'Perene', 'Tóxica para humanos e pets, contém um óleo que causa irritação na pele e problemas gastrointestinais se ingerido. Prefere alta umidade e temperaturas amenas.', 'https://www.shutterstock.com/search/croton-plant', 50, 80, 15, 32, 30, 100, false),
('Dama-da-noite', 'Nyctaginaceae', 'América tropical', 'Planta trepadeira com flores perfumadas à noite. Usada para perfumar jardins e áreas de lazer.', 'Perene', 'Não tóxica para humanos ou pets. O florescimento é principalmente à noite, atraindo mariposas. Prefere sol pleno e solo bem drenado.', 'https://www.gettyimages.com.br/fotos/cestrum-nocturnum', 50, 80, 18, 28, 100, 500, false),
('Dracena', 'Asparagaceae', 'África tropical', 'Planta de interior, amplamente utilizada na decoração. Purificadora de ar.', 'Perene', 'Severamente tóxica para cães e gatos, pois contém saponinas em suas folhas. A ingestão pode causar vômitos, diarreia e desidratação. Tolerante a baixa luminosidade.', 'https://br.freepik.com/fotos-gratis/dracaena-fragrans-em-um-vaso-de-ceramica_181624-34441.htm', 30, 60, 15, 28, 50, 200, false),
('Espada-de-São-Jorge', 'Asparagaceae', 'África Ocidental', 'Planta de interior e purificadora de ar. Decoração de interiores. Tem significado místico.', 'Perene', 'Tóxica para humanos e pets (saponinas). Purifica o ar de substâncias como benzeno e xileno. Extremamente resistente e de baixa manutenção.', 'https://www.shutterstock.com/search/sansevieria-trifasciata', 30, 50, 10, 32, 50, 120, false),
('Ficus lyrata', 'Moraceae', 'África Ocidental', 'Árvore ornamental de interior, conhecida por suas folhas grandes em formato de lira.', 'Perene', 'Tóxica para humanos e pets, a seiva leitosa contém oxalato de cálcio e causa irritação na pele, boca e trato gastrointestinal se ingerida.', 'https://www.shutterstock.com/search/ficus-lyrata', 40, 70, 18, 32, 100, 300, false),
('Filodendro Xanadu', 'Araceae', 'América do Sul, Brasil', 'Planta ornamental para ambientes internos ou forração em áreas sombreadas.', 'Perene', 'Moderadamente tóxica devido a cristais de oxalato de cálcio. É purificadora de ar e resistente a ambientes internos climatizados. Sua classificação atual é *Thaumatophyllum xanadu*.', 'https://www.plantalivre.pt/pt/plantas/plantas-de-interior/philodendron-xanadu', 60, 80, 18, 29, 50, 100, false),
('Flor-da-fortuna', 'Crassulaceae', 'Madagascar', 'Suculenta de flores coloridas e duráveis para decoração de interiores.', 'Perene', 'Não tóxica. Também conhecida como Calandiva. É de baixíssima manutenção, ideal para iniciantes. Simboliza felicidade e prosperidade. Atrai abelhas e borboletas.', 'https://viplant.pt/app/uploads/2024/09/VIPLANT-38-scaled-1.jpg', 30, 60, 15, 30, 20, 40, false),
('Girassol', 'Asteraceae', 'América do Norte', 'Flor ornamental cultivada em jardins e para produção de sementes e óleo.', 'Anual', 'Não tóxico para humanos e animais. As sementes podem ser usadas como petisco para pets com moderação. Simboliza felicidade e vitalidade e atrai polinizadores.', 'https://www.shutterstock.com/search/helianthus', 40, 70, 18, 35, 100, 300, false),
('Hera', 'Araliaceae', 'Europa e Ásia', 'Planta trepadeira, usada como cobertura de solo, em cercas e vasos suspensos.', 'Perene', 'Toxicidade média por ingestão ou contato, contendo saponinas que causam irritação na pele, delírio e vômito. Adaptável e de baixa manutenção.', 'https://www.shutterstock.com/search/hedera-helix', 50, 80, 5, 35, 20, 200, false),
('Hibisco', 'Malvaceae', 'Ásia Tropical, Havaí', 'Arbusto ornamental de flores grandes e vistosas. Usado em jardins e cercas vivas.', 'Perene', 'Não tóxico, mas pode causar toxicidade se ingerido em grandes quantidades. Atrai polinizadores como beija-flores. Requer alta umidade e temperaturas elevadas.', 'https://br.freepik.com/fotos/hibiscus-rosa-sinensis', 60, 85, 20, 38, 250, 500, false),
('Hortênsia', 'Hydrangeaceae', 'Ásia', 'Arbusto ornamental de flores grandes e coloridas. Usado em jardins e vasos.', 'Perene', 'Tóxica para humanos e pets, contendo glicosídeos cianogênicos que causam problemas gastrointestinais. Floresce na primavera e verão. O pH do solo interfere na cor da flor.', 'https://www.shutterstock.com/search/hydrangea-macrophylla', 60, 85, 15, 25, 50, 200, false),
('Ipê-amarelo', 'Bignoniaceae', 'América do Sul', 'Árvore ornamental de grande valor para arborização urbana e paisagismo.', 'Decídua', 'Tolerância baixa a moderada, a casca possui propriedades medicinais anti-inflamatórias. Floresce intensamente na estação seca. Símbolo do Brasil. Atrai polinizadores.', 'https://www.shutterstock.com/search/handroanthus-chrysanthus', 40, 70, 15, 30, 400, 1500, false),
('Jacarandá-mimoso', 'Bignoniaceae', 'América do Sul, Argentina, Brasil', 'Árvore de paisagismo urbano. Usado para ornamentar ruas, calçadas, praças e parques.', 'Perene', 'Não tóxico para humanos e animais. As raízes não são agressivas, o que o torna ideal para calçadas. Madeira de alta qualidade usada na indústria moveleira. Floresce na primavera.', 'https://www.jardineiro.net/wp-content/uploads/2011/05/jacaranda_mimosaefolia.jpg', 40, 70, 15, 30, 400, 1500, false),
('Jasmim-manga', 'Apocynaceae', 'México, América Central', 'Arbusto ornamental com flores perfumadas em várias cores. Usado em jardins e parques.', 'Perene', 'Tóxica para cães. A seiva leitosa é irritante para a pele e mucosas, podendo causar problemas gastrointestinais se ingerida. Floresce na maior parte do ano e é uma planta tolerante à seca.', 'https://www.sitiodamata.com.br/especies-de-plantas/jasmim-manga-plumeria-rubra.html', 30, 60, 15, 35, 200, 800, false),
('Jiboia', 'Araceae', 'Ilhas Salomão, Oceania', 'Planta de interior, trepadeira, ideal para vasos suspensos. Purificadora de ar.', 'Perene', 'Levemente tóxica para humanos e severamente tóxica para pets (oxalato de cálcio), podendo ser fatal para gatos. Purifica o ar de poluentes como benzeno e formaldeído. De crescimento rápido e fácil cuidado.', 'https://unsplash.com/es/s/fotos/jiboia-planta', 40, 80, 16, 32, 20, 600, false),
('Lantana', 'Verbenaceae', 'América tropical', 'Arbusto ornamental de flores coloridas. Usado em jardins e cercas vivas.', 'Perene', 'Frutos verdes e folhas são tóxicos para humanos e animais. Pode causar insuficiência hepática em gado. As flores mudam de cor com o tempo, atraindo borboletas.', 'https://img.freepik.com/fotos-gratis/lantana-camara-em-um-jardim-tropical_181624-34543.jpg', 40, 75, 18, 35, 50, 150, false),
('Lavanda', 'Lamiaceae', 'Regiões mediterrâneas', 'Erva aromática, usada em paisagismo, cosméticos e culinária.', 'Perene', 'Tóxica para cães e gatos, pois contém linalol e acetato de linalila que causam problemas gastrointestinais. Atrai abelhas e borboletas. É resistente à seca.', 'https://www.gettyimages.com/photos/lavender-angustifolia-close-up', 30, 50, 10, 28, 30, 90, false),
('Lírio', 'Liliaceae', 'Oriente', 'Flor ornamental, muito usada em arranjos e jardins.', 'Perene', 'Extremamente tóxico para gatos, a ingestão de qualquer parte pode causar insuficiência renal aguda e ser fatal. Floresce na primavera e verão. É uma das flores mais populares do Brasil.', 'https://www.shutterstock.com/search/lilium-spp', 50, 80, 15, 28, 30, 90, false),
('Lírio da Paz', 'Araceae', 'América Central e do Sul', 'Planta de interior, purificadora de air. Simboliza paz e harmonia.', 'Perene', 'Tóxica para humanos e pets (oxalato de cálcio). Pode causar irritação na boca e dificuldade de respiração. Purifica o air de poluentes e tem cuidados simples.', 'https://unsplash.com/pt-br/s/fotografias/l%C3%ADrio-da-paz', 50, 80, 15, 30, 30, 90, false),
('Magnólia', 'Magnoliaceae', 'América do Norte e Ásia', 'Árvore ornamental de grande porte. Usada em parques e grandes jardins.', 'Perene', 'Não tóxica para humanos e animais. É uma opção segura para paisagismo em áreas com pets. Flores grandes e perfumadas.', 'https://br.freepik.com/fotos/magnolia-grandiflora', 50, 80, 15, 28, 300, 1000, false),
('Manacá-da-Serra', 'Melastomataceae', 'Brasil', 'Arbusto ornamental de flores que mudam de cor. Usado em jardins e arborização urbana.', 'Perene', 'Todas as partes da planta são tóxicas para animais se consumidas. Atrai borboletas. As flores nascem brancas, se tornam rosa e finalizam o ciclo na cor roxa.', 'https://img.freepik.com/fotos-gratis/manaca-da-serra-em-um-jardim-tropical_181624-34540.jpg', 50, 80, 15, 28, 200, 800, false),
('Mandacaru', 'Cactaceae', 'Brasil', 'Cacto nativo do Semiárido brasileiro. Usado como alimento e para fins medicinais.', 'Perene', 'Não tóxico para humanos e animais. Bem adaptado a climas áridos. Apresenta propriedades antimicrobianas e anti-inflamatórias. Frutos comestíveis.', 'https://img.freepik.com/fotos-gratis/mandacaru-em-uma-paisagem-semiarida_181624-34541.jpg', 5, 20, 20, 40, 100, 400, false),
('Manjericão', 'Lamiaceae', 'África e Ásia', 'Erva aromática e culinária, usada em vasos e jardins.', 'Anual', 'Pode causar dores de estômago e diminuir a fertilidade se ingerido em excesso. O manjericão orgânico apresenta menor toxicidade. Atrai polinizadores.', 'https://img.freepik.com/fotos-gratis/manjericao-em-um-jardim-tropical_181624-34542.jpg', 40, 70, 15, 30, 30, 60, false),
('Maranta Burle Marx', 'Marantaceae', 'Brasil', 'Planta de interior ornamental. Ideal para casas com pets.', 'Perene', 'Não tóxica para animais de estimação. Conhecida como "planta rezadeira" porque suas folhas se fecham à noite, em um movimento característico. Prefere alta umidade e luz indireta.', 'https://www.floramorumby.com.br/wp-content/uploads/2021/04/maranta-burle-marx.jpg', 60, 80, 18, 28, 20, 40, false),
('Onze-horas', 'Portulacaceae', 'América do Sul', 'Planta suculenta de porte rasteiro. Floresce abundantemente em sol pleno.', 'Anual/Perene', 'Não tóxica, mas pode causar problemas gastrointestinais em grandes quantidades. As flores coloridas abrem ao sol e fecham à noite, atraindo polinizadores como abelhas.', 'https://images.unsplash.com/photo-1598642345091-a18541a54772?q=80&w=1740&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', 20, 50, 20, 35, 10, 30, false),
('Orquídea', 'Orchidaceae', 'Ásia tropical', 'Flor ornamental popular, conhecida por sua beleza e longevidade.', 'Perene', 'Não tóxica para cães e gatos, mas a ingestão de grandes quantidades pode causar problemas gastrointestinais. É segura para a maioria dos pets.', 'https://img.freepik.com/fotos-gratis/orquidea-phalaenopsis-em-um-jardim-tropical_181624-34544.jpg', 50, 70, 18, 28, 20, 60, false),
('Pacová', 'Araceae', 'Brasil', 'Planta ornamental e de fácil cultivo, ideal para ambientes internos com pouca luz.', 'Perene', 'Tóxica para cães e gatos, e com toxinas que também afetam humanos. Suas folhas grandes e caule gordinho armazenam água, tornando-a resistente à seca. Classificação botânica correta é *Philodendron martianum*.', 'https://www.foliage-factory.com/cdn/shop/files/Philodendron-martianum-Pacova-aka-Fatboy-2-2_1024x1024.jpg?v=1707923761', 60, 80, 18, 27, 60, 90, false),
('Palmeira Areca', 'Arecaceae', 'Madagascar', 'Palmeira ornamental de interior e exterior, conhecida pela sua elegância.', 'Perene', 'Não tóxica para humanos e pets. Purificadora de ar, remove poluentes como benzeno e formaldeído. De crescimento rápido e ideal para criar divisórias em ambientes internos.', 'https://img.freepik.com/fotos-gratis/palmeira-areca-em-um-vaso-de-ceramica_181624-34442.jpg', 50, 80, 18, 30, 100, 400, false),
('Palmeira Ráfis', 'Arecaceae', 'Ásia', 'Palmeira ornamental de interior e exterior. Usada para decoração em jardins e vasos.', 'Perene', 'Não tóxica para pets, conhecida como a planta da prosperidade. Purificadora de ar, absorve e elimina poluentes. Requer pouca água e iluminação indireta.', 'https://www.shutterstock.com/search/raphis-excelsa', 40, 70, 15, 28, 80, 300, false),
('Palmeira Leque', 'Arecaceae', 'Oceania', 'Palmeira de pequeno porte, usada em vasos e jardins internos bem iluminados. Adiciona um toque tropical ao ambiente.', 'Perene', 'Levemente tóxica para humanos e animais. Suas folhas grandes e plissadas são a característica mais marcante. Prefere umidade alta, calor e luz indireta.', 'https://www.shutterstock.com/search/licuala', 60, 90, 20, 32, 80, 300, false),
('Pata-de-elefante', 'Asparagaceae', 'México', 'Planta suculenta com tronco inchado que armazena água. Usada em vasos e jardins de pedra. Ornamental e de fácil cultivo.', 'Perene', 'Não tóxica para humanos e pets. Conhecida por sua resistência à seca e baixa manutenção. Seu tronco pode chegar a 2,5 metros de altura em seu habitat natural.', 'https://www.istockphoto.com/photos/beaucarnea-recurvata', 20, 40, 15, 35, 50, 250, false),
('Peperômia', 'Piperaceae', 'Américas tropicais', 'Planta de interior de fácil cultivo, ideal para vasos e terrários.', 'Perene', 'Não tóxica para cães e gatos, sendo uma opção segura para lares com animais. Conhecida por suas folhas suculentas e capacidade de reter água.', 'https://img.freepik.com/fotos-gratis/peperomia-em-um-vaso-de-ceramica_181624-34445.jpg', 40, 70, 18, 30, 15, 50, false),
('Pleomele', 'Asparagaceae', 'África, Madagascar', 'Planta arbustiva de interior. Usada no paisagismo e em decoração.', 'Perene', 'Tóxica para cães e gatos (saponinas), pode causar irritação no sistema digestivo. O nome botânico é *Dracaena reflexa*. Tolera sol pleno, meia-sombra ou luz difusa.', 'https://www.jardineiro.net/wp-content/uploads/2011/05/dracaena_reflexa.jpg', 60, 90, 18, 24, 80, 250, false),
('Rosa', 'Rosaceae', 'Eurásia', 'Flor ornamental, muito usada em jardins e buquês.', 'Perene', 'Não tóxica para humanos e animais. Os espinhos podem causar danos físicos, mas a planta em si é segura. É um símbolo de amor e beleza.', 'https://img.freepik.com/fotos-gratis/rosas-vermelhas-em-um-jardim-tropical_181624-34545.jpg', 50, 80, 15, 28, 50, 200, false),
